def is_simple_power(x: int, n: int) -> bool:
    if x == 1:
        return True

    if n == 1:
        return x == 1  # Only 1 is a power of 1

    if n == 0 or x == 0:
        return False

    if x < 0 and n < 0:
        return is_power_of(-x, -n)

    if x < 0 or n < 0:
        return False

    return is_power_of(x, n)
