def find_biggest_even(start: int, end: int) -> int:
    """
    Find the biggest even number in the range [start, end].
    If no even number exists in this range, return -1.
    """
    # Iterate from end to start to find the largest even number
    for num in range(end, start - 1, -1):
        if num % 2 == 0:
            return num
    return -1

