def find_biggest_even(start: int, end: int) -> int:
    """
    Find the biggest even integer between start and end inclusive.
    Returns -1 if no even number is found in the range.
    """
    # Adjust the end to be the largest even number less than or equal to end
    if end % 2 != 0:
        end -= 1
    for i in range(end, start - 1, -2):
        # Iterate backwards to find the largest even number
        if i % 2 == 0:
            return i
    return -1
