def contains_whitespace(txt: str) -> bool:
    assert isinstance(txt, str), f"Input must be a string, got {type(txt)}"
    return any(char.isspace() for char in txt)

def split_on_whitespace(txt: str) -> list:
    assert isinstance(txt, str), f"Input must be a string, got {type(txt)}"
    return txt.split()

def contains_comma(txt: str) -> bool:
    assert isinstance(txt, str), f"Input must be a string, got {type(txt)}"
    return ',' in txt

def split_on_comma(txt: str) -> list:
    assert isinstance(txt, str), f"Input must be a string, got {type(txt)}"
    return [word.strip() for word in txt.split(',') if word.strip()]

def count_odd_lowercase_letters(txt: str) -> int:
    assert isinstance(txt, str), f"Input must be a string, got {type(txt)}"
    odd_order_letters = 'acegikmoqsuwy'
    return sum(1 for char in txt if char in odd_order_letters)
